$(document).ready(function() {
    $('body').css('overflow', 'hidden');

    var download_file = function(text, filename)
    {
        var blob = new Blob([text], { type: "text/plain" });

        var link = document.createElement("a");
            link.href = URL.createObjectURL(blob);

            link.download = (filename + ".txt");
            link.innerHTML = "Download";

        document.body.appendChild(link);

        link.click();
        link.remove();
    };

    ajax('GET', '/api/candidates/list', {}, function (a) {
        if(a.success){
            // console.log(a.data);
            document.cookie = "Authorization="+a.token+"; path=/";
            $('#name').html(a.data.name_cn);
            $('#account').html(a.data.account);
            $('#school_name').html(a.data.school_cn);
            var box = "";
            $('#upcomingEvent').empty();
            $('#pastEvent').empty();
            for (const i in a.data.competition)
            {
                const _this = a.data.competition[i];
                if (_this.status < 4)
                {
                    box = '<div class="col-11" style="background-color: black; height: 180px; margin: 10px; padding: 1em; color: #FFFFFF;">';
                    box += '<h5 class="mb-0 mt-0" style="color: #FFFFFF;">' + _this.tag + '</h5>';
                    box += '<hr>';
                    box += '<div class="d-flex flex-wrap">';
                        box += '<div class="col-12 col-sm-8">';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['sessionTime'] + '：</h5>';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + moment.unix(_this.date).format("YYYY 年 MM 月 DD 日 HH:mm") + '</h5>';
                        box += '</div>';
                        const diff = moment.unix(_this.date).diff(new Date(), 'minutes');
                        if ( diff < 10 && diff > -120)
                        {
                            box += '<div class="col-12 col-sm-4">';
                                    box += '<div class="d-flex justify-content-center align-items-end"><button class="btn btn-light start-btn" data-id="' + _this.roomid + '" data-cid="' + _this.cid + '">' + langDict['enter'] + langDict['competition'] + '</button><div>';
                            box += '</div>';
                        }
                    box += '</div>';
                    box += '</div>';
                    $('#upcomingEvent').append(box);

                }
                else
                {
                    box = '<div class="col-11" style="background-color: black; min-height: 220px; margin: 10px; padding: 1em; color: #FFFFFF;">';
                    box += '<h5 class="mb-0 mt-0" style="color: #FFFFFF;"><span class="badge badge-warning">' + langDict['evalution'] + '</span>&emsp;' + _this.tag + '</h5>';
                    box += '<hr>';
                    box += '<div class="d-flex flex-wrap">';
                        box += '<div class="col-12 col-sm-9">';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['sessionTime'] + '：' + moment.unix(_this.date).format("YYYY 年 MM 月 DD 日 HH:mm") + '</h5>';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['title'] + '：' + translate(a.data.competition[0].title).split('$?')[0] + '</h5>';
                            if (a.data.competition[0].title.split('$?')[1]) box += '<h5 class="mt-0 mb-1" style="color: #FFFFFF; text-indent: 3em;">' + translate(a.data.competition[0].title).split('$?')[1] + '</h5>';
                            box += '<div class="d-flex flex-wrap mt-2">';
                                box += '<div class="col-12 col-sm-6">';
                                box += '<p class="mb-1" style="color: #FFFFFF;"><span class="badge badge-secondary">' + langDict['pos'] + '：</span></p>';
                                box += '<p class="mb-1" style="color: #FFFFFF;">' + ((_this.usr[0].role == 1) ? _this.usr[0].name_cn : _this.usr[1].name_cn) + '</p>';
                                box += '<p class="mb-1" style="color: #FFFFFF;">' + ((_this.usr[0].role == 1) ? _this.usr[0].school_cn : _this.usr[1].school_cn) + '</p>';
                                box += '</div>';
                                box += '<div class="col-12 col-sm-6">';
                                box += '<p class="mb-1" style="color: #FFFFFF;"><span class="badge badge-secondary">' + langDict['neg'] + '：</span></p>';
                                box += '<p class="mb-1" style="color: #FFFFFF;">' + ((_this.usr[0].role == 1) ? _this.usr[1].name_cn : _this.usr[0].name_cn) + '</p>';
                                box += '<p class="mb-1" style="color: #FFFFFF;">' + ((_this.usr[0].role == 1) ? _this.usr[1].school_cn : _this.usr[0].school_cn) + '</p>';
                                box += '</div>';
                            box += '</div>';
                        box += '</div>';
                        box += '<div class="col-12 col-sm-3">';
                            box += '<div class="d-flex justify-content-center align-items-end flex-column">';
                            const sid = (_this.usr[0].mid == a.data.id) ? _this.usr[0].id : _this.usr[1].id;
                            if (_this.status >= 4) 
                                box += '<button class="btn btn-light download-article-btn" data-sid="' + sid +'">' + langDict['downloadArticle'] + '</button>';
                            if (_this.status >= 5) box += '<button class="btn btn-light download-judge-btn"" data-sid="' + sid +'">' + langDict['judgeFeedback'] + '</button>';
                            box += '<div>';
                        box += '</div>';
                    box += '</div>';
                    box += '</div>';
                    $('#pastEvent').append(box);
                }

            }
        }else
            $.growl.error({message: a.message})
    }); 

    
    $(document).on('click', '.start-btn', function () {
        const sid = $(this).data('id');
        const cid = $(this).data('cid');
        location.href = "/candidate/" + cid + "/session/" + sid;
    });

    $(document).on('click', '.download-article-btn', function () {
        
        ajax('GET', '/api/articles/download/' + $(this).data('sid'), {}, function(a){
            var text = "";

                text += langDict['sessionName'] + '：' + a.data.tag + '\n';
                text += langDict['sessionTime'] + '：' + moment.unix(a.data.date).format("YYYY-MM-DD HH:mm:ss") + '\n';
                text += langDict['title'] + '：\n' + a.data.title.split('$?')[0] + '\n';
                if (a.data.title.split('$?')[1]) text += a.data.title.split('$?')[1] + '\n';

                text += '\n---------------------------------------------------------------------------------\n\n';
                text += langDict['mine'] + langDict['argument'] + '：\n';
                text += trimquotes(a.data.article[0].content);
                
                text += '\n---------------------------------------------------------------------------------\n\n';
                text += langDict['mine'] + langDict['rebuttal'] + '：\n';
                text += trimquotes(a.data.article[1].content);
            
                download_file(text, a.data.tag);
        })
    });

    var pad_space = function(str, len)
    {
        var space = "";
        const str_len = str.length;
        console.log(str, str_len, len);
        const differnce = len - str_len;

        if (differnce > 0)
        {
            for (var i = 0; i <= (differnce / 2); i ++)
                space += " ";
        }
        var ans = space + str + space;
        return (ans);
    };
    
    $(document).on('click', '.download-judge-btn', function () {
        
        ajax('GET', '/api/judges/download/' + $(this).data('sid'), {}, function(a){
            var text = "";

                text += langDict['sessionName'] + '：' + a.data.tag + '\n';
                text += langDict['sessionTime'] + '：' + moment.unix(a.data.date).format("YYYY-MM-DD HH:mm:ss") + '\n';
                text += langDict['title'] + '：\n' + a.data.title.split('$?')[0] + '\n';
                if (a.data.title.split('$?')[1]) text += a.data.title.split('$?')[1] + '\n';

                text += '\n====================================================================================\n\n';

                for (const i in a.data.judge)
                {
                    const _this = a.data.judge[i];

                    text += langDict['score_1_title'] + ': ' + _this.score_1 + ' /200\n';
                    text += langDict['score_2_title'] + ': ' + _this.score_2 + ' /200\n';
                    text += langDict['score_3_title'] + ': ' + _this.score_3 + '  /50 \n';
                    text += langDict['score_4_title'] + ': ' + _this.score_4 + '  /50 \n\n';
                    text += langDict['sum'] + ': ' + _this.score + '/500\n\n';
                    text += langDict['judgeComment'] + ':\n';
                    text += _this.comment + ':\n';

                    text += '\n====================================================================================\n\n';

                }
                download_file(text, a.data.tag + langDict['judgeFeedback']);
        })
    });
 });