$(document).ready(function() {
    $('body').css('overflow', 'hidden');

    var download_file = function(text, filename)
    {
        var blob = new Blob([text], { type: "text/plain" });

        var link = document.createElement("a");
            link.href = URL.createObjectURL(blob);

            link.download = (filename + ".txt");
            link.innerHTML = "Download";

        document.body.appendChild(link);

        link.click();
        link.remove();
    };

    ajax('GET', '/api/judges/list', {}, function (a) {
        if(a.success){
            console.log(a.data);
            document.cookie = "Authorization="+a.token;
            $('#name').html(a.data.name_cn);
            $('#account').html(a.data.account);
            $('#school_name').html(a.data.school_cn);
            var box = "";
            $('#upcomingEvent').empty();
            $('#pastEvent').empty();
            for (const i in a.data.competition)
            {
                const _this = a.data.competition[i];
                if (_this.status < 2)
                {
                    box = '<div class="col-11" style="background-color: black; height: 180px; margin: 10px; padding: 1em; color: #FFFFFF;">';
                    box += '<h5 class="mb-0 mt-0" style="color: #FFFFFF;">' + _this.tag + '</h5>';
                    box += '<hr>';
                    box += '<div class="d-flex flex-wrap">';
                        box += '<div class="col-12 col-sm-8">';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['sessionTime'] + '：</h5>';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + moment.unix(_this.date).format("YYYY 年 MM 月 DD 日 HH:mm") + '</h5>';
                        box += '</div>';
                        const diff = moment.unix(_this.date).diff(new Date(), 'minutes');
                        if ( _this.status == 1)
                        {
                            box += '<div class="col-12 col-sm-4">';
                                    box += '<div class="d-flex justify-content-center align-items-end"><button class="btn btn-light start-btn" data-id="' + _this.roomid + '" data-cid="' + _this.cid + '">' + langDict['enter'] + langDict['judge'] + '</button><div>';
                            box += '</div>';
                        }
                    box += '</div>';
                    box += '</div>';
                    $('#upcomingEvent').append(box);

                }
                else
                {
                    box = '<div class="col-11" style="background-color: black; min-height: 220px; margin: 10px; padding: 1em; color: #FFFFFF;">';
                    box += '<h5 class="mb-0 mt-0" style="color: #FFFFFF;"><span class="badge badge-warning">' + langDict['evalution'] + '</span>&emsp;' + _this.tag + '</h5>';
                    box += '<hr>';
                    box += '<div class="d-flex flex-wrap">';
                        box += '<div class="col-12 col-sm-9">';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['sessionTime'] + '：' + moment.unix(_this.date).format("YYYY 年 MM 月 DD 日 HH:mm") + '</h5>';
                            box += '<h5 class="mb-1 mt-0" style="color: #FFFFFF;">' + langDict['title'] + '：' + translate(a.data.competition[0].title).split('$?')[0] + '</h5>';
                            if (a.data.competition[0].title.split('$?')[1]) box += '<h5 class="mt-0 mb-1" style="color: #FFFFFF; text-indent: 3em;">' + translate(a.data.competition[0].title).split('$?')[1] + '</h5>';
                        box += '</div>';
                        box += '<div class="col-12 col-sm-3">';
                            box += '<div class="d-flex justify-content-center align-items-end flex-column">';
                            if (_this.status > 5) box += '<button class="btn btn-light">' + langDict['judgeFeedback'] + '</button>';
                            box += '<div>';
                        box += '</div>';
                    box += '</div>';
                    box += '</div>';
                    $('#pastEvent').append(box);
                }

            }
        }else
            $.growl.error({message: a.message})
    }); 

    
    $(document).on('click', '.start-btn', function () {
        const sid = $(this).data('id');
        const cid = $(this).data('cid');
        location.href = "/judge/" + cid + "/session/" + sid;
    });

    $(document).on('click', '.download-article-btn', function () {
        
        ajax('GET', '/api/articles/download/' + $(this).data('sid'), {}, function(a){
            var text = "";

                text += langDict['sessionTime'] + '：' + a.data.tag + '\n';
                text += langDict['sessionTime'] + '：' + moment.unix(a.data.date).format("YYYY-MM-DD HH:mm:ss") + '\n';
                text += langDict['title'] + '：\n' + a.data.title.split('$?')[0] + '\n';
                if (a.data.title.split('$?')[1]) text += a.data.title.split('$?')[1] + '\n';

                text += '\n---------------------------------------------------------------------------------\n\n';
                text += langDict['mine'] + langDict['argument'] + '：\n';
                text += trimquotes(a.data.article[0].content);
                
                text += '\n---------------------------------------------------------------------------------\n\n';
                text += langDict['mine'] + langDict['rebuttal'] + '：\n';
                text += trimquotes(a.data.article[1].content);
            
                download_file(text, a.data.tag);
        })
    });
 });